function toJson(){var e=document.getElementById("xmlArea").value;if(!empty(e)){var n=parseXml(e),t=xml2json(n," ");document.getElementById("jsonArea").value=t.replace("undefined","")}}function toXml(){var e=document.getElementById("jsonArea").value;if(!empty(e)){var n=json2xml(JSON.parse(e)," ");document.getElementById("xmlArea").value=n}}function dlJson(){download(document.getElementById("jsonArea").value,"json.json","text/json")}function dlXml(){download(document.getElementById("xmlArea").value,"xml.xml","text/xml")}
